mapper.update:
update customer_tb 
set pass = #{pass}, phone_no = #{phone_no},name = #{name}, customer_addr = #{customer_addr}, email = #{email},
birth = #{birth,jdbcType=DATE}, ps_question = #{ps_question}, ps_answer = #{ps_answer}, state = #{state} 
where customer_id = #{customer_id}

mapper.select:
select *
		from(select ROWNUM rn, ct.*
		        FROM (select * from customer_tb where state !='관리자') ct where name like '%' || #{name} || '%' )


		rn BETWEEN 1 AND #{remainNo} 



		rn BETWEEN #{remainNo}-9 AND #{remainNo}

select *
		from(select ROWNUM rn, ct.*
		        FROM (select * from customer_tb where state= #{state}) ct where name like '%' || #{name} || '%' )


		rn BETWEEN 1 AND #{remainNo} 


		rn BETWEEN #{remainNo}-9 AND #{remainNo}
